import React from 'react';
import './css/App.css';
import Table from './containers/table';
import Bomb from './containers/bomb';
import GameOver from './containers/gameOver';


class App extends React.Component {

  constructor(props){
    super(props);
    this.state = {
      word: "wonder",
      wrongAnswers: 9,
    }
    this.incrementWrongAnswers = this.incrementWrongAnswers.bind(this)
  }

  incrementWrongAnswers(){
    this.setState({
      wrongAnswers: this.state.wrongAnswers - 1
    })
    console.log(this.state.wrongAnswers)
  }

  render(){
  return (
    <div className="App">
    {this.state.wrongAnswers !== 0 && <Bomb wrongAnswers={this.state.wrongAnswers} />}
    {this.state.wrongAnswers <= 0 && <GameOver/>}
    <Table word={this.state.word} burnFuse={this.incrementWrongAnswers} />
    </div>
  );
}
}

export default App;
