import React from 'react';
import '../css/letter.css';

class Letter extends React.Component{
    //this component will produce an input the will
    //take one letter, check if it is correct and 
    // change the isCorrect boolean accordingly

    constructor(props){
        super(props);
        this.state = {
            letter: '',
            correct: false,
        };

        this.checkLetter = this.checkLetter.bind(this);

    }

    checkLetter(event){
        //checks to see if the letter is correct for the position
        for (let i = 0; i < this.props.word.length; i++){
            if (event.target.value === this.props.word[i] && i + 1 === this.props.position){
                this.setState({
                    correct: true,
                    letter: event.target.vaule
                })
            }

        }
        if (!this.state.correct){
            console.log("burn Fuse!")
        }
    }
    render(){
        return(
            <div className="letterBox">
                <input 
                type="text" 
                onChange={this.checkLetter} 
                value={this.state.letter} 
                size="2"
                />
            </div>
        );
    }  
}

export default Letter;
