import React from 'react';
import '../css/bomb.css';

class Bomb extends React.Component{
    constructor(props){
        super(props);
        this.state = {
            holder: true,
        }

    this.createFuse = this.createFuse.bind(this)
    }

    createFuse(){
        let children = [];
        let burnedStart;

        if (this.props.wrongAnswers > 0) {
            burnedStart = 9 - this.props.wrongAnswers;
        }
        for (let i = 1; i < 9; i++){
            if (i < burnedStart){
            children.push(<div className={`fuse${i}`}></div>)
            } else if (i => burnedStart){
                children.push(<div className={`fuse${i} burned${i}`}></div>)
            }
            // if(i > this.props.wrongAnswers){
            //     children.push(<div className={`fuse${i}`}></div>)
            // } else {

            //     children.push(<div className={`fuse${i}`}></div>)
            // }


        }

        return children
    }

    render(){
        return(

            <div className="bomb">

                <div className="fuseBody">
                {this.createFuse()}
                </div>
                <div className="top"></div>
                <div className="bombBody">
                    <div className="glare"></div>
                </div>

            </div>

        )
    }
}
export default Bomb;
