import React from 'react';
import '../css/bomb.css';

class Bomb extends React.Component{
    constructor(props){
        super(props);
        this.state = {
            holder: true,
        }

    this.createFuse = this.createFuse.bind(this)
    }

    createFuse(){
        let children = []
        for (let i = 1; i <= 8; i++){
            if(i < this.props.wrongAnswers){
                children.push(<div className={`fuse${i} burned${i}`}></div>)
            } else {
                children.push(<div className={`fuse${i}`}></div>)
            }


        }

        return children
    }

    render(){
        return(
            <div className="bomb">
                {/* <div className="fuse8"></div> */}

                <div className="fuseBody">
                {this.createFuse()}
                </div>


                {/* <div className="fuse7"></div>
                <div className="fuse6"></div>
                <div className="fuse5"></div>
                <div className="fuse4"></div>
                <div className="fuse3"></div>
                <div className="fuse2"></div>
                <div className="fuse1"></div> */}
            </div>
        )
    }
}
export default Bomb;
