import React from 'react';
import '../css/bomb.css';

class Bomb extends React.Component{
    constructor(props){
        super(props);
        this.state = {
            gameOver: false,
        }

    this.createFuse = this.createFuse.bind(this)
    this.displayBomb = this.displayBomb.bind(this)
    }

    createFuse(){
        let children = []
        if (this.props.wrongAnswers === 9){
            this.setState({
                gameOver: true,
            })
        }
        for (let i = 1; i <= 8; i++){
            if(i < this.props.wrongAnswers){
                children.push(<div className={`fuse${i}`}></div>)
            } else {

                children.push(<div className={`fuse${i} burned${i}`}></div>)
            }


        }

        return children
    }

    displayBomb(){
        return
    }
    render(){
        return(
            <div className="bomb">
               { !this.state.gameOver &&
                `<div className="fuseBody">
                {this.createFuse()}
                </div>
                <div className="top"></div>
                <div className="bombBody">
                    <div className="glare"></div>
                </div>`}

            </div>
        )
    }
}
export default Bomb;
